    public class ParseHTML
    {
        public StreamWriter swOut = new StreamWriter("out.txt");
        public void Go(string strHTML)
        {
            //Traverse DOM
            //Tag (class) name, tag properties, tag inner-text (NOT IT'S CHILD'S INNERTEXT!), then
            // It's children, recursively

            // Load HTML document
            //var web = new HtmlWeb();
            //var document = web.Load("https://example.com");
            var document = new HtmlAgilityPack.HtmlDocument();
            document.LoadHtml(strHTML);

            // Start traversal from the root node
            TraverseDOM(document.DocumentNode, 0);

            swOut.Close();
        }

        public void TraverseDOM(HtmlNode node, int depth)
        {
            // Indentation for readability
            string indent = new string(' ', depth * 4);

            // Tag (class) name
            Console.WriteLine($"{indent}Tag Name: {node.Name}");
            swOut.WriteLine($"{indent}Tag Name: {node.Name}");

            // Tag properties
            Console.Write($"{indent}Properties: ");
            swOut.WriteLine($"{indent}Properties: ");

            foreach (var attribute in node.Attributes)
            {
                Console.Write($"{attribute.Name}={attribute.Value} ");
                swOut.WriteLine($"{attribute.Name}={attribute.Value} ");
            }
            Console.WriteLine();
            swOut.WriteLine();

            // Tag inner-text (NOT IT'S CHILD'S INNERTEXT)
            Console.WriteLine($"{indent}Inner Text: {node.InnerText.Trim()}");
            swOut.WriteLine($"{indent}Inner Text: {node.InnerText.Trim()}");

            // Traverse its children recursively
            foreach (var child in node.ChildNodes)
            {
                TraverseDOM(child, depth + 1);
            }
        }
    }
